(321, 186, 'second_conten', '<h3>ABOUT THE JOB</h3>\r\nDo you enjoy thinking strategically, generating ideas, and leading impactful programs? You’re the person we are looking for! Impact Force is a nonprofit working on social and mental behavior change projects with lasting impact. Our team members operate from Ukraine, Germany, Spain and Thailand. We are growing rapidly and need an enthusiastic, experienced, digitally aware applicant to join our team. The ideal candidate is passionate about the nonprofit sector, all things technology, is a creative problem-solver with a proven track record of leading successful mental health and biodiversity related projects.'),
(322, 186, '_second_conten', 'field_66479d8501c7d'),
(323, 186, 'left_column_title', 'KEY RESPONSIBILITIES'),
(324, 186, '_left_column_title', 'field_66479dde01c7f'),
(325, 186, 'left_column_list_0_list_item', 'Project planning and management: Developing and managing project plans, timelines, and budgets to ensure projects are completed on time, within budget, and meet project goals.'),
(326, 186, '_left_column_list_0_list_item', 'field_66479e0901c81'),
(327, 186, 'left_column_list_1_list_item', 'Team coordination and management: Coordinating project team members and partners, to ensure everyone is aligned and working towards the same goals.'),
(328, 186, '_left_column_list_1_list_item', 'field_66479e0901c81'),
(329, 186, 'left_column_list_2_list_item', 'Monitoring and evaluation: Monitoring project progress and evaluating project outcomes to ensure the project is meeting its goals and making a positive impact.'),
(330, 186, '_left_column_list_2_list_item', 'field_66479e0901c81'),
(331, 186, 'left_column_list_3_list_item', 'Reporting and communication: Preparing regular progress reports for project stakeholders, including funders, board members, and other key stakeholders, and communicating project updates and successes to external stakeholders.'),
(332, 186, '_left_column_list_3_list_item', 'field_66479e0901c81'),
(333, 186, 'left_column_list_4_list_item', 'Risk management: Identifying and managing project risks, including financial, operational, and reputational risks, to ensure the project is successful.'),
(334, 186, '_left_column_list_4_list_item', 'field_66479e0901c81'),
(335, 186, 'left_column_list_5_list_item', 'Grant writing and fundraising: Researching and writing grant proposals to secure funding for the project, and working with the nonprofit’s fundraising team to identify and secure other sources of funding.'),
(336, 186, '_left_column_list_5_list_item', 'field_66479e0901c81'),
(337, 186, 'left_column_list_6_list_item', 'Resource allocation: Allocating resources, including funding, staff, and volunteers, to ensure the project has the resources it needs to be successful.'),
(338, 186, '_left_column_list_6_list_item', 'field_66479e0901c81'),
(339, 186, 'left_column_list_7_list_item', 'Partnership development: Developing and maintaining relationships with partners, including other nonprofits, government agencies, and other stakeholders, to ensure the project is successful and has a broad impact.'),
(340, 186, '_left_column_list_7_list_item', 'field_66479e0901c81'),
(341, 186, 'left_column_list', '8'),
(342, 186, '_left_column_list', 'field_66479df701c80'),
(343, 186, 'left_column', ''),
(344, 186, '_left_column', 'field_66479dc401c7e'),
(345, 186, 'right_column_title', 'KEY REQUIREMENTS'),
(346, 186, '_right_column_title', 'field_66479e1801c83'),
(347, 186, 'right_column_list_0_list_item', 'Experience working on programs related to mental health and biodiversity. '),
(348, 186, '_right_column_list_0_list_item', 'field_66479e1801c85'),
(349, 186, 'right_column_list_1_list_item', 'Experience working in multinational organizations or foundations, or nonprofit.'),
(350, 186, '_right_column_list_1_list_item', 'field_66479e1801c85'),
(351, 186, 'right_column_list_2_list_item', 'Experience in project management, preferably in a nonprofit setting.'),
(352, 186, '_right_column_list_2_list_item', 'field_66479e1801c85'),
(353, 186, 'right_column_list_3_list_item', 'Deep knowledge of sectors such as human development, philanthropy and fundraising, government and public policy, technology and innovation.'),
(354, 186, '_right_column_list_3_list_item', 'field_66479e1801c85'),
(355, 186, 'right_column_list_4_list_item', 'Work effectively in fast paces, deadline driven environments both as a team member and individually with minimum supervision.'),
(356, 186, '_right_column_list_4_list_item', 'field_66479e1801c85'),
(357, 186, 'right_column_list_5_list_item', 'Ability to influence, negotiate with, and persuade others. Must be flexible and demonstrate strong judgment and decision-making skills.'),
(358, 186, '_right_column_list_5_list_item', 'field_66479e1801c85'),
(359, 186, 'right_column_list_6_list_item', 'Ability to understand complex issues and data, summarize them and articulate them in simple, effective ways.'),
(360, 186, '_right_column_list_6_list_item', 'field_66479e1801c85'),
(361, 186, 'right_column_list_7_list_item', 'Ability to work within international teams across different geographies.'),
(362, 186, '_right_column_list_7_list_item', 'field_66479e1801c85'),
(363, 186, 'right_column_list_8_list_item', 'Native or bilingual proficiency in English and Ukrainian for both written and spoken skills.'),
(364, 186, '_right_column_list_8_list_item', 'field_66479e1801c85'),
(365, 186, 'right_column_list_9_list_item', 'Maintain highest personal levels of ethical conduct, confidentiality and integrity.'),
(366, 186, '_right_column_list_9_list_item', 'field_66479e1801c85'),
(367, 186, 'right_column_list_10_list_item', 'Bachelor’s degree in a relevant field.'),
(368, 186, '_right_column_list_10_list_item', 'field_66479e1801c85'),
(369, 186, 'right_column_list_11_list_item', 'Proficiency in Google Workspace, Microsoft Office and project management software.'),
(370, 186, '_right_column_list_11_list_item', 'field_66479e1801c85'),
